import React, { Component } from 'react'
import axios from 'axios'
import PostItem from './PostItem';

export default class PostList extends Component {

    constructor(props) {
        super(props)

        this.state = {
             post :[]
        }
    }

    componentDidMount(){
        axios.get('https://jsonplaceholder.typicode.com/posts')
        .then(response =>{
            this.setState({
                post : response.data
            })
        })
        .catch(error =>{
            console.log(error)
        })
    }

    render() {
        return (
            <div>
                <label className="text-danger">Posts From API</label>

                {this.state.post.map((post_item)=>
                      <p key={post_item.id} >{post_item.id}</p> 

                )}



            </div>
        )
    }
}
