import React, { Component } from 'react'
import { Button, Form, FormGroup,  Input } from 'reactstrap';
import axios from 'axios'

export default class SearchMovie extends Component {

         constructor(props) {
             super(props)

             this.state = {
                   movie :'',
                   search :[]

             }
         }


   searchHandler = event =>{
       this.setState({
           movie: event.target.value
       })
   }
   componentDidMount(){
       this.onSubmitHandler()
   }

    onSubmitHandler = event=>{
        event.preventDefault()
         let search_movie = this.state.movie
         const url = 'http://www.omdbapi.com/?s='+search_movie+'&apikey=95632709';
        axios.get(url)
        .then(response =>{
            console.log(response.data.Search)
            this.setState({
                search : response.data.Search
            })
        })
        .catch(error =>{
            console.log(error)
        })

        console.log(url)
    }


    render() {
        return (

                <Form inline onSubmit={this.onSubmitHandler}>

                    <FormGroup >

                    <Input type="text" value={this.state.movie} onChange={this.searchHandler} placeholder="Search for a movie!!" />
                    </FormGroup>
                    <Button type='submit'>Search</Button>
                </Form>

        )
    }
}
