import React, { Component } from 'react'
import {CardColumns, Card, CardImg,CardBody,CardTitle,CardDeck,Row ,Col, Button,CardText,CardSubtitle} from 'reactstrap';
import MovieDetails from './MovieDetails';
export default class MovieList extends Component {

    constructor(props) {
        super(props)

        this.state = {

        }
    }

    render() {
            const {Title,Poster,imdbID} = this.props.sch
        return (

            <div>
                    <CardDeck style={{display: 'flex', flexDirection: 'row'}}>
                         <Card  body outline color="success" style={{flex: 1}}>
                            <CardImg top width="100%" src={Poster} alt="Card image cap" />
                            <CardBody>
                            <CardTitle >{Title}</CardTitle>
                            <Button outline color="success" size="sm" onClick={this.props.onDetails.bind(this,imdbID)} >Details</Button>
                            </CardBody>
                        </Card>                 
                    </CardDeck>

               <MovieDetails modal = {this.props.modal} details ={this.props.details} onDetails = {this.props.onDetails}/>  

            </div>
        )
    }
}
