import React, { Component } from 'react'
import { Button, Form, FormGroup,  Input } from 'reactstrap';
import axios from 'axios'
import { Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle} from 'reactstrap';

export default class SearchMovie extends Component {

         constructor(props) {
             super(props)

             this.state = {
                   movie :'',
                   search :[]

             }
         }


   searchHandler = event =>{
       this.setState({
           movie: event.target.value
       })
   }

    onSubmitHandler = event=>{
        event.preventDefault()
        axios.get('http://www.omdbapi.com/?s='+this.state.movie+'&apikey=95632709')
        .then(response =>{
            console.log(response.data.Search)
            this.setState({
                search : response.data.Search
            })
        })
        .catch(error =>{
            console.log(error)
        })
    }


    render() {
        return (

            <div>
                <Form inline onSubmit={this.onSubmitHandler}>
                    <FormGroup >
                    <Input type="text" value={this.state.movie} onChange={this.searchHandler} placeholder="Search for a movie!!" />
                    </FormGroup>
                    <Button type='submit'>Search</Button>
                </Form>



                    {this.state.search.map(item =>
                        <Card>
                            <CardImg top width="100%" src={item.Poster} alt="Card image cap" />
                            <CardBody>
                            <CardTitle key={item.imdbID}>{item.Title}</CardTitle>
                            <CardSubtitle key={item.imdbID}>{item.Title}</CardSubtitle>
                            <CardText key={item.imdbID}>{item.Title}</CardText>
                            <Button >Button</Button>
                            </CardBody>
                        </Card>
                    )}

            </div>
        )
    }
}

Card.propTypes = {
    // Pass in a Component to override default element
    tag: PropTypes.oneOfType([PropTypes.func, PropTypes.string]),
    inverse: PropTypes.bool,
    color: PropTypes.string,
    body: PropTypes.bool,
    className: PropTypes.string
  };
