import React, { Component } from 'react'
import { Table } from 'reactstrap';
import axios from 'axios'

export default class PostList extends Component {

    constructor(props) {
        super(props)

        this.state = {
             post :[]
        }
    }

    componentDidMount(){
        axios.get('https://jsonplaceholder.typicode.com/posts')
        .then(response =>{
            this.setState({
                post : response.data
            })
        })
        .catch(error =>{
            console.log(error)
        })
    }

    render() {
        return (
            <div>
                <label className="text-danger">Posts From API</label>

    <Table hover>
        <thead>
          <tr>
            <th>Title</th>

          </tr>
        </thead>
        <tbody>
          <tr></tr>

             {
                 this.state.post.map(( item)=>
                 <tr key ={item.id}>{item.title}</tr>
                 )
             }



        </tbody>
 </Table>
            </div>
        )
    }
}
