import React, { Component } from 'react'
import { Button, Form, FormGroup,  Input } from 'reactstrap';
import axios from 'axios'
import MovieList from './MovieList';

export default class SearchMovie extends Component {

         constructor(props) {
             super(props)

             this.state = {
                   movie :'',
                   search :[]

             }
         }


   searchHandler = event =>{
       this.setState({
           movie: event.target.value
       })
   }

    onSubmitHandler = event=>{
        event.preventDefault()
        axios.get('http://www.omdbapi.com/?s='+this.state.movie+'&apikey=95632709')
        .then(response =>{
            console.log(response.data.Search)
            this.setState({
                search : response.data.Search
            })
        })
        .catch(error =>{
            console.log(error)
        })
    }


    render() {
        return (

            <div>
                <Form inline onSubmit={this.onSubmitHandler}>
                    <FormGroup >
                    <Input type="text" value={this.state.movie} onChange={this.searchHandler} placeholder="Search for a movie!!" />
                    </FormGroup>
                    <Button type='submit'>Search</Button>
                </Form>

                <Table hover>
                    <thead>
                    <tr>
                        <th>ID</th>
                        <th>Title</th>
                        <th>Body</th>
                    </tr>
                    </thead>
                    <tbody>

                    {this.state.search.map(item =>
                        <tr>
                        <td key={item.imdbID}>{item.imdbID}</td>
                        <td key={item.Title}>{item.Title}</td>
                        <td key={item.Type}>{item.Type}</td>
                        </tr>
                    )}
                    </tbody>
                </Table>
            </div>
        )
    }
}
