import React, { Component } from 'react'
import { Card, CardImg,CardBody,CardTitle,CardDeck,Row ,Col, Button} from 'reactstrap';
import MovieDetails from './MovieDetails';
export default class MovieList extends Component {

    constructor(props) {
        super(props)

        this.state = {

        }
    }

    render() {

        return (
            <div>
            <Row>
                <Col>
                    <CardDeck style={{display: 'flex', flexDirection: 'row'}} >
                         <Card style={{flex: 1}} >
                            <CardImg top width="20%" src={this.props.sch.Poster} alt="Card image cap" />
                            <CardBody>
                            <CardTitle >{this.props.sch.Title}</CardTitle>
                            <Button outline color="success" size="sm" onClick={this.props.onDetails.bind(this,this.props.sch.imdbID)} >Details</Button>
                            </CardBody>
                        </Card>                 
                    </CardDeck>
                </Col>
               <MovieDetails modal = {this.props.modal} details ={this.props.details} onDetails = {this.props.onDetails}/>  
            </Row>
            </div>
        )
    }
}
