import React, { Component } from 'react'
import { Button, Form, FormGroup,  Input } from 'reactstrap';
import './Movies.css'
import axios from 'axios'
import { Card, CardImg,CardBody,
    CardTitle,CardDeck } from 'reactstrap';
    import PropTypes from 'prop-types';

export default class SearchMovie extends Component {

         constructor(props) {
             super(props)

             this.state = {
                   movie :'',
                   search :[]

             }
         }


   searchHandler = event =>{
       this.setState({
           movie: event.target.value
       })
   }

    onSubmitHandler = event=>{
        event.preventDefault()
        axios.get('http://www.omdbapi.com/?s='+this.state.movie+'&apikey=95632709')
        .then(response =>{
            console.log(response.data.Search)
            this.setState({
                search : response.data.Search
            })
        })
        .catch(error =>{
            console.log(error)
        })
    }


    render() {
        return (

            <div >
                <Form inline onSubmit={this.onSubmitHandler} className="App-Movie" >
                    <FormGroup className="mb-4 mr-sm-4 mb-sm-0" >
                    <Input type="text" value={this.state.movie} onChange={this.searchHandler} placeholder="Search for a movie!!" />
                    </FormGroup>
                    <Button type='submit' outline color="success" size="sm" >Search</Button>
                </Form>
                <CardDeck style={{display: 'flex', flexDirection: 'row'}}>
                    {this.state.search.map(item =>

                                <Card style={{flex: 1}}>
                                    <CardImg top width="40%" src={item.Poster} alt="Card image cap" />
                                    <CardBody>
                                    <CardTitle key={item.imdbID}>{item.Title}</CardTitle>
                                    <Button outline color="success" size="sm" >Details</Button>
                                    </CardBody>
                                </Card>

                    )}
                    </CardDeck>
            </div>
        )
    }
}

Card.propTypes = {
    // Pass in a Component to override default element
    tag: PropTypes.oneOfType([PropTypes.func, PropTypes.string]),
    inverse: PropTypes.bool,
    color: PropTypes.string,
    body: PropTypes.bool,
    className: PropTypes.string
  };
